import style from "./Card.module.css";
import { useEffect } from "react";
import { connect } from "react-redux";
import { addFav, removeFav } from "../../Redux/Actions/actions";
import { useState } from "react";
import { Link } from "react-router-dom";

const { contenedor, h3, h2, img } = style;

function Card(props) {
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = (event) => {
    event.preventDefault();
    if (isFav) {
      setIsFav(false);
      props.removeFavCard(props.id);
    } else {
      setIsFav(true);
      props.addFavCard(props);
    }
  };

  useEffect(() => {
    props.miFavoriteCard.forEach((fav) => {
      if (fav.id === props.id) {
        setIsFav(true);
      }
    });
  }, [props.miFavoriteCard]);

  return (
    <div className={contenedor}>
      <div>
        <button onClick={() => props.onClose(props.id)}>
          Close carta {props.id}
        </button>
      </div>
      {isFav ? (
        <button onClick={handleFavorite}>‚ù§Ô∏è</button>
      ) : (
        <button onClick={handleFavorite}>ü§ç</button>
      )}
      <Link to={`/detail/${props.id}`}>
        <h2 className={h2}>{props.name}</h2>
      </Link>
      <div className={h3}>
        <h3>{props.species}</h3>
        <h3>{props.gender}</h3>
        <img src={props.image} alt="" className={img} />
      </div>
    </div>
  );
}

export function mapDispatchToProps(dispatch) {
  return {
    addFavCard: (char) => dispatch(addFav(char)),
    removeFavCard: (id) => dispatch(removeFav(id)),
  };
}

export function mapStateToProps(state) {
  return {
    myFavoriteCard: state.myFavorites,
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Card);
