export const removeFav = async (id) => {
  const endpoint = "http://localhost:3001/rickandmorty/fav/" + id;
  return async (dispatch) => {
    try {
      await axios.delete(endpoint).then(({ data }) => {
        return dispatch({
          type: "REMOVE_FAV",
          payload: data,
        });
      });
    } catch (error) {
      console.log(error.message);
    }
  };
};
